'use strict';

hexo.extend.filter.register( 'after_post_render', page => {
	if ( !hexo.projectGlobals.common || !hexo.projectGlobals.common.verbose ) {
		return;
	}
	page.time = { start: process.hrtime() };
}, 1 );
