'use strict';

const splitLongname = require( './split-longname' );

module.exports = longname => {
	if ( !longname ) {
		return '';
	}

	const parts = splitLongname( longname );

	return [ parts.packageName, ...parts.directoryNames, parts.moduleName ].join( '/' );
};
