'use strict';

/**
 * Escapes string
 * @param str
 * @returns {String}
 */
module.exports = str => {
	if ( typeof str !== 'string' ) {
		str = String( str );
	}

	return str.replace( /&/g, '&amp;' )
		.replace( /</g, '&lt' );
};
