'use strict';

module.exports = ( ctx, customConfig ) => {
	ctx.extend.filter.register( 'after_init', () => {
		ctx.config = Object.assign( ctx.config, customConfig );
		// jscs:disable
		ctx.public_dir = customConfig.public_dir;
		// jscs:enable
	} );
};
